ee:variables.ee:set-variable:
%dw 2.0
output application/java
var rand=random()
---
if (rand > 0.8)
round( (random() * 100000))
else if (rand > 0.3)
round( (random() * 10000))
else
round( (random() * 1000))

ee:message.ee:set-payload:
%dw 2.0
output application/json
var lastTransaction = vars.acccountTransactions[-1]
---

  {
    "id" : uuid(),
    "number": vars.accountNumber,
    "balance": {
      "amount": lastTransaction.balance,
      "currency": "USD"
      },
    "available_balance": {
      "amount": lastTransaction.balance,
      "currency": "USD"
      }
  }

%dw 2.0 import * from dw::core::Arrays
output application/json
var beginning_balance=vars.beginBalance
var transactions=read("data/transaction.csv", "application/csv")
fun calc_balance(payload) = payload reduce ((item, accumulator=beginning_balance) -> if (item.credit_vs_debit == 'C') accumulator-item.amount else accumulator + item.amount) --- payload map (trans, index) -> { transaction_id: trans.transaction_id, account_no: trans.account_no, transaction_date: trans.transaction_date, transaction_time: trans.transaction_time, amount: trans.amount, credit_vs_debit: trans.credit_vs_debit, check_no: trans.check_no, transaction_details: trans.transaction_details, transaction_type: trans.transaction_type, transaction_sector: trans.transaction_sector, branch_id: trans.branch_id, currency: "USD", balance: calc_balance(slice (payload, 0, index+1)) }
